#!/bin/bash

#--------------------------------------------------------------#
# Variables
#--------------------------------------------------------------#
dn=$1
usernm=$2
osx=$4
rootdn="uid=$usernm,ou=users,$dn"
pwplain=$3
hostname=`hostname -f`
uuid=`uuidgen`

hostname=$(echo "$dn" | sed 's/dc\=//g' | sed 's/,/./g' )

tmpdir=./tmp
log=$tmpdir/output.log
[ -d $tmpdir ] || mkdir $tmpdir
echo "" >$log

#--------------------------------------------------------------#
# Install ldap
#--------------------------------------------------------------#
echo -ne '#                 Updating sources  (1%)\r'
apt-get update -y >>$log

echo "Installing LDAP" >>$log
echo -ne '#                  Installing LDAP  (5%)\r'
apt-get install -y --force-yes slapd ldap-utils >>$log
sleep .2

passwd=`slappasswd -s $pwplain`

#--------------------------------------------------------------#
echo "Adding schemas" >>$log 2>&1
echo -ne '#######             Adding schemas (25%)\r'

ldapadd -Y EXTERNAL -H ldapi:/// -f cosine.ldif >>$log 2>&1
ldapadd -Y EXTERNAL -H ldapi:/// -f inetorgperson.ldif >>$log 2>&1
ldapadd -Y EXTERNAL -H ldapi:/// -f nis.ldif >>$log 2>&1
ldapadd -Y EXTERNAL -H ldapi:/// -f misc.ldif >>$log 2>&1
ldapadd -Y EXTERNAL -H ldapi:/// -f samba.ldif >>$log 2>&1
if [ $osx == 1 ]; then
	ldapadd -Y EXTERNAL -H ldapi:/// -f apple.ldif >>$log 2>&1
fi
sleep .1

#--------------------------------------------------------------#
# database.ldif
#--------------------------------------------------------------#

cat <<EOF > $tmpdir/database.ldif
# Load dynamic backend modules
dn: cn=module{0},cn=config
objectClass: olcModuleList
cn: module{0}
olcModulePath: /usr/lib/ldap
olcModuleLoad: {0}back_hdb

# Create directory database
dn: olcDatabase={1}hdb,cn=config
objectClass: olcDatabaseConfig
objectClass: olcHdbConfig
olcDatabase: {1}hdb
olcDbDirectory: /var/lib/ldap
olcSuffix: $dn
olcRootDN: cn=$usernm,$dn
olcRootPW: $passwd
olcAccess: {0}to attrs=userPassword,shadowLastChange by dn="cn=$usernm,$dn" write by dn="$rootdn" write by anonymous auth by self write by * none
olcAccess: {1}to dn.base="" by * read
olcAccess: {2}to * by dn="cn=$usernm,$dn" write by dn="$rootdn" write by * read
olcLastMod: TRUE
olcDbCheckpoint: 512 30
olcDbConfig: {0}set_cachesize 0 2097152 0
olcDbConfig: {1}set_lk_max_objects 1500
olcDbConfig: {2}set_lk_max_locks 1500
olcDbConfig: {3}set_lk_max_lockers 1500
olcDbIndex: uid,gidNumber,sambasid,uidNumber pres,eq
olcDbIndex: cn,sn,mail,givenName,memberUid pres,eq,approx,sub
olcDbIndex: objectClass eq
EOF

##### MAC ONLY indexes
if [ $osx == 1 ]; then
# add indexes for faster performance in WGM
cat <<EOF >> $tmpdir/database.ldif
olcDbIndex: apple-group-realname,apple-realname pres,eq,approx,sub
olcDbIndex: apple-generateduid,apple-group-memberguid,apple-ownerguid pres,eq
EOF
fi

cat <<EOF >> $tmpdir/database.ldif
################################
#        Modifications
################################

dn: cn=config
changetype: modify

dn: olcDatabase={-1}frontend,cn=config
changetype: modify
delete: olcAccess

dn: olcDatabase={0}config,cn=config
changetype: modify
add: olcRootDN
olcRootDN: cn=$usernm,cn=config

dn: olcDatabase={0}config,cn=config
changetype: modify
add: olcRootPW
olcRootPW: $passwd

# No further changes to config permitted if uncommented
# dn: olcDatabase={0}config,cn=config
# changetype: modify
# delete: olcAccess

EOF

####################################
#         Mini DIT
####################################
cat <<EOF> $tmpdir/dit.ldif

# Tree root
dn: $dn
objectClass: dcObject
objectclass: organization
o: $hostname
dc: $(echo "$hostname" | sed 's/[^a-z0-9].*$//g')
description: Tree root

dn: cn=$usernm,$dn
objectClass: simpleSecurityObject
objectClass: organizationalRole
cn: $usernm
userPassword: $passwd
description: LDAP administrator

# Populating
dn: ou=users,$dn
ou: users
objectClass: organizationalUnit
objectClass: top

dn: ou=groups,$dn
ou: groups
objectClass: organizationalUnit
objectClass: top

dn: ou=mounts,$dn
ou: mounts
objectClass: organizationalUnit
objectClass: top

#Adding Admin group
dn: cn=admin,ou=groups,$dn
cn: admin
gidNumber: 80
objectClass: top
objectClass: posixGroup
memberUid: $usernm

#Adding user
dn: $rootdn
objectClass: inetOrgPerson
objectClass: posixAccount
objectClass: shadowAccount
objectClass: organizationalPerson
uid: $usernm
sn: Administrator
givenName: Directory
cn: Directory Administrator
uidNumber: 1000
gidNumber: 80
userPassword: $passwd
loginShell: /bin/bash
homeDirectory: /home/$usernm
title: System Administrator
EOF

